mapper.select:
SELECT CST.CS_NAME AS csName
		,CST.CS_MEMBER_CODE AS csMemberCode
FROM TBL_RESERV RSV
JOIN TBL_CST CST ON RSV.MEMBER_CODE = CST.CS_MEMBER_CODE
WHERE RSV.LESSON_CODE = #{lessonCode}
		AND RSV.RS_CANCEL_YN = FALSE;

SELECT CST.CS_MEMBER_CODE
FROM TBL_RESERV RSV
JOIN TBL_CST CST ON RSV.MEMBER_CODE = CST.CS_MEMBER_CODE
WHERE RSV.LESSON_CODE = #{lessonCode}
AND RSV.RS_CANCEL_YN = FALSE;

SELECT CST.CS_NAME
FROM TBL_RESERV RSV
JOIN TBL_CST CST ON RSV.MEMBER_CODE = CST.CS_MEMBER_CODE
WHERE RSV.LESSON_CODE = #{lessonCode}
AND RSV.RS_CANCEL_YN = FALSE;

SELECT COUNT(*) FROM TBL_RESERV
WHERE LESSON_CODE = #{lessonCode} 
		AND RS_CANCEL_YN = FALSE;

SELECT COUNT(*) FROM TBL_ATTEND
WHERE LESSON_CODE = #{lessonCode}
AND (AT_ATTENDANCE1_YN = TRUE OR AT_ATTENDANCE2_YN = TRUE OR AT_ATTENDANCE3_YN = TRUE OR 
		AT_ATTENDANCE4_YN = TRUE OR AT_ATTENDANCE5_YN = TRUE OR AT_ATTENDANCE6_YN = TRUE);

SELECT COUNT(*)
FROM TBL_ATTEND
WHERE LESSON_CODE = #{lessonCode}
AND (AT_ATTENDANCE1_YN = FALSE OR AT_ATTENDANCE2_YN = FALSE OR AT_ATTENDANCE3_YN = FALSE OR 
		AT_ATTENDANCE4_YN = FALSE OR AT_ATTENDANCE5_YN = FALSE OR AT_ATTENDANCE6_YN = FALSE);
